/*
*  BaseView - A class with some common functionality
*  for interface view code.
*/

class BaseView {
	constructor() {
		this.state_vars = {};
	}

	init_state(vars) {
		this.state_vars = vars;
	}

	get_state_vars() {
		return this.state_vars;
	}

	state(key, value) {
		if (value !== undefined) {
			this.state_vars[key] = value;
		} else {
			return this.state_vars[key];
		}
	}
}
exports.BaseView = BaseView;
